import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from "@material-ui/core/styles";

/**
 * Product Icons in the Store page
 * @param {name, categories, onClick, img_url, alt, rating} ProductIcon
 */

const useStyles = makeStyles(() => ({
    container: {
        width: '10rem',
        height: '12rem',
        border: '1px solid #000',
        textAlign: 'center',
    },
    img: {
        height: '50%',
        marginTop: '10%',
    },
    name: {
        display: 'block'
    },
    rating_text: {
        display: 'block'
    }
}));

const ProductIcon = (props) => {
    const classes = useStyles();
    const { name, categories, img_url, alt, onClick, rating, ...other } = props;

    return (
        <div
            className={classes.container}
            onClick={() => onClick()}
            {...other}>
            <img className={classes.img} src={require(`../images/${img_url}`)} alt={alt}></img>
            <span className={classes.name}>{name}</span>
            {rating != null &&
                (
                    <span className={classes.rating_text}>{rating}</span>
                )
            }
        </div>
    );
}

ProductIcon.propTypes = {
    name: PropTypes.string.isRequired,
    categories: PropTypes.array.isRequired,
    onClick: PropTypes.func.isRequired,
    img_url: PropTypes.string.isRequired,
    alt: PropTypes.string.isRequired,
    rating: PropTypes.number
}

export default ProductIcon;
