    }

    useEffect(() => {
        setCurrPage(1);
    }, [currProducts]);

    return (
        <div>

            <NavBar
                logoOnClick={() => logoClickHandler()}
                storeOnClick={() => storeClickHandler()}
                accountOnClick={() => accountClickHandler()}
                cartOnClick={() => cartClickHandler()}
            />

