import React, { useState } from 'react';
import PropTypes from 'prop-types';
import { makeStyles } from "@material-ui/core/styles";

/**
 * Product Icons in the Store page
 * @param {name, categories, onClick, img_url, alt} ProductIcon
 */

const useStyles = makeStyles(() => ({
    container: {
        width: '10rem',
        height: '12rem',
        backgroundColor: '#841',
        textAlign: 'center',
    },
    img: {
        height: '70%',
        marginTop: '10%'
    }
}));

const ProductIcon = (props) => {
    const classes = useStyles();
    const { name, categories, img_url, alt, onClick, ...other } = props;

    return (
        <div className={classes.container}>
            <img className={classes.img} src={require(`../images/${img_url}`)} alt={alt}></img>
        </div>
    );
}

ProductIcon.propTypes = {
    name: PropTypes.string.isRequired,
    categories: PropTypes.array.isRequired,
    onClick: PropTypes.func.isRequired,
    img_url: PropTypes.string.isRequired,
    alt: PropTypes.string.isRequired,
}

export default ProductIcon;
