import React from "react";
import { withStyles, makeStyles } from "@material-ui/core/styles";
import TextField from "@material-ui/core/TextField";

// Textfield Customized with CSS
const use_textfield_styles = makeStyles(theme => ({
    root: {
        margin: theme.spacing(1),
        "& .MuiOutlinedInput-root": {
            "& fieldset": {
                borderColor: theme.palette.primary.main,
            },
        },
    },
}));

const use_form_styles = makeStyles(theme => ({
    root: {
        display: "flex",
        flexWrap: "wrap"
    },
}));

function CustomizedInputs(props) {
  const form_classes = use_form_styles();
  const textfield_classes = use_textfield_styles();
  const {id, label, ...other} = props

  return (
    <form className={form_classes.root} noValidate>
        <TextField
            className={textfield_classes.root}
            label={label}
            variant="outlined"
            id={id}
            InputProps={{
                color: "white"
            }}
            {...other}
        />
    </form>
  );
}

export default CustomizedInputs;
