import React from "react";
import PropTypes from 'prop-types'
import { makeStyles } from "@material-ui/core/styles";

/**
 * Store Icons in the Store Selection page
 * @param {name, categories, onClick, img_url, alt} StoreIcon
 */

const useStyles = makeStyles(() => ({
    round_img: {
        width: '128px',
        height: '128px',
        boxShadow: '0 0 0 1px rgba(0,0,0,.15)',
        borderRadius: '50%',
    },

}));

const StoreIcon = (props) => {
    const classes = useStyles();
    const {name, categories, img_url, alt, onClick, ...other} = props;

    return (
        <div onClick={()=>onClick()} {...other}>
            <img style={classes.round_img} src={require( `../images/stores/${ img_url }` )} alt={alt}></img>
            <span>{name}</span>
            <span>{categories.join(' · ')}</span>
        </div>
    );
}

StoreIcon.propTypes = {
    name: PropTypes.string.isRequired,
    categories: PropTypes.array.isRequired,
    onClick: PropTypes.func.isRequired,
    img_url: PropTypes.string.isRequired,
    alt: PropTypes.string.isRequired,
}

export default StoreIcon;
